const allPosts = $input.all();
console.log('\n=== POSTS ===');
console.log('Total:', allPosts.length);

const processedPosts = [];
for (const item of allPosts) {
  const post = item.json;
  const title = post.title || post.authorTitle || post.projTitle || post.name || 'Untitled';
  const content = post.content || post.description || post.projDescription || post.body || '';
  const fullText = title + ' ' + content;

  processedPosts.push({
    json: {
      postId: post.id || post._id || 'unknown',
      title: title,
      content: content,
      fullText: fullText
    }
  });
}

return processedPosts;

// Parse Firestore response - GENERAL for any number of students
const response = $input.first().json;

console.log('\n=== PARSING STUDENTS ===');

if (!response.documents || response.documents.length === 0) {
  console.log('‚ùå No students in database');
  return [];
}

console.log('Raw documents:', response.documents.length);

const students = [];

for (const doc of response.documents) {
  const fields = doc.fields || {};
  const student = { _id: doc.name.split('/').pop() };

  // Convert Firestore format to normal JSON
  for (const [key, value] of Object.entries(fields)) {
    if (value.stringValue !== undefined) {
      student[key] = value.stringValue;
    } else if (value.integerValue !== undefined) {
      student[key] = parseInt(value.integerValue);
    } else if (value.doubleValue !== undefined) {
      student[key] = value.doubleValue;
    } else if (value.booleanValue !== undefined) {
      student[key] = value.booleanValue;
    } else if (value.arrayValue && value.arrayValue.values) {
      student[key] = value.arrayValue.values.map(v => {
        if (v.stringValue) return v.stringValue;
        if (v.integerValue) return parseInt(v.integerValue);
        if (v.doubleValue) return v.doubleValue;
        return v;
      });
    } else if (value.mapValue && value.mapValue.fields) {
      const mapObj = {};
      for (const [mk, mv] of Object.entries(value.mapValue.fields)) {
        if (mv.stringValue) mapObj[mk] = mv.stringValue;
        else if (mv.integerValue) mapObj[mk] = parseInt(mv.integerValue);
        else if (mv.doubleValue) mapObj[mk] = mv.doubleValue;
      }
      student[key] = mapObj;
    }
  }

  students.push({ json: student });
}

console.log('‚úÖ Parsed:', students.length, 'students');
return students;

// Add post data and build interests for EACH student
const postData = $('Embed Post').first().json;
const postEmbedding = postData.data[0].embedding;
const postTitle = $('Loop Each Post').first().json.title;
const postContent = $('Loop Each Post').first().json.content;
const postId = $('Loop Each Post').first().json.postId;

const students = $input.all();

console.log('\n=== ENRICHING STUDENTS ===');
console.log('Students:', students.length);
console.log('Post:', postTitle);

const enrichedStudents = [];

for (const student of students) {
  const data = student.json;

  // Build comprehensive interest text
  const parts = [];

  // Helper to add field if exists
  const addField = (field) => {
    if (data[field]) {
      if (Array.isArray(data[field])) {
        const filtered = data[field].filter(x => x && String(x).trim());
        if (filtered.length > 0) parts.push(filtered.join(' '));
      } else if (String(data[field]).trim()) {
        parts.push(String(data[field]));
      }
    }
  };

  // Add all relevant fields
  addField('keywords');
  addField('researchArea');
  addField('interests');
  addField('projDescription');
  addField('projTitle');
  addField('skills');
  addField('bio');
  addField('title');
  addField('expDescription');
  addField('achDescription');
  addField('department');
  addField('degree');

  const interestText = parts.join(' ');

  // Only include if has email and interests
  if (data.email && interestText.length > 10) {
    enrichedStudents.push({
      json: {
        email: data.email,
        name: data.name || data.email.split('@')[0],
        interests: interestText,
        postTitle: postTitle,
        postContent: postContent,
        postId: postId,
        postEmbedding: postEmbedding
      }
    });
  }
}

console.log('Valid students:', enrichedStudents.length);

if (enrichedStudents.length === 0) {
  return [{ json: { noStudents: true } }];
}

return enrichedStudents;

// Batch all interests for efficient embedding
const students = $input.all();

console.log('\n=== BATCHING FOR EMBEDDING ===');
console.log('Students to batch:', students.length);

if (students.length === 0 || students[0].json.noStudents) {
  return [{ json: { noStudents: true } }];
}

const allInterests = [];
const studentMetadata = [];

for (const student of students) {
  allInterests.push(student.json.interests);
  studentMetadata.push(student.json);
}

console.log('Interests in batch:', allInterests.length);

return [{
  json: {
    interests: allInterests,
    students: studentMetadata
  }
}];

// Split batch back to individual student items
const response = $input.first().json;
const embeddings = response.data;
const batchData = $('Batch All Interests').first().json;
const students = batchData.students;

console.log('\n=== SPLITTING EMBEDDINGS ===');
console.log('Embeddings received:', embeddings.length);
console.log('Students to split:', students.length);

if (embeddings.length !== students.length) {
  console.log('‚ö†Ô∏è COUNT MISMATCH!');
  console.log('Using minimum:', Math.min(embeddings.length, students.length));
}

const results = [];
const count = Math.min(students.length, embeddings.length);

for (let i = 0; i < count; i++) {
  results.push({
    json: {
      ...students[i],
      studentEmbedding: embeddings[i].embedding
    }
  });
}

console.log('‚úÖ Created', results.length, 'individual items');
return results;

// Semantic matching with cosine similarity
function cosineSimilarity(a, b) {
  if (!a || !b || a.length !== b.length) return 0;
  let dot = 0, normA = 0, normB = 0;
  for (let i = 0; i < a.length; i++) {
    dot += a[i] * b[i];
    normA += a[i] * a[i];
    normB += b[i] * b[i];
  }
  return dot / (Math.sqrt(normA) * Math.sqrt(normB));
}

console.log('\n=== SEMANTIC MATCHING ===');

const THRESHOLD = 0.25; // 25% as requested
const students = $input.all();
const matched = [];
const allScores = [];

console.log('Evaluating:', students.length, 'students');
console.log('Threshold:', (THRESHOLD * 100) + '%\n');

for (const item of students) {
  const s = item.json;

  if (!s.studentEmbedding || !s.postEmbedding) {
    console.log('‚ö†Ô∏è Missing embedding for:', s.email);
    continue;
  }

  const similarity = cosineSimilarity(s.postEmbedding, s.studentEmbedding);
  const percent = Math.round(similarity * 100);

  allScores.push({ email: s.email, percent: percent });

  const status = percent >= (THRESHOLD * 100) ? '‚úÖ MATCH' : '‚ùå';
  console.log(`${s.name} (${s.email})`);
  console.log(`  ${percent}% ${status}`);

  if (similarity >= THRESHOLD) {
    matched.push({
      json: {
        email: s.email,
        name: s.name,
        postTitle: s.postTitle,
        postContent: s.postContent,
        similarity: percent + '%'
      }
    });
  }
}

console.log('\n=== SUMMARY ===');
console.log('Total evaluated:', allScores.length);
console.log('Matches found:', matched.length);

if (allScores.length > 0) {
  const avg = Math.round(allScores.reduce((sum, s) => sum + s.percent, 0) / allScores.length);
  const max = Math.max(...allScores.map(s => s.percent));
  const min = Math.min(...allScores.map(s => s.percent));
  console.log(`Range: ${min}%-${max}% (avg: ${avg}%)`);
}

if (matched.length === 0) {
  console.log('\nüí° No matches above 25% threshold');
  return [{ json: { noMatches: true, scores: allScores } }];
}

return matched;

const r = $input.first().json;
console.log('\n‚úÖ EMAIL SENT');
console.log('To:', r.to);
console.log('ID:', r.id);
return [$input.first()];
